# Technical Analysis Indicators and Calculations\n\nimport numpy as np\nimport pandas as pd\n\nclass TechnicalAnalysis:\n    def __init__(self, data):\n        # Data should be a DataFrame with a 'Close' price column\n        self.data = data\n\n    def moving_average(self, period):\n        return self.data['Close'].rolling(window=period).mean()\n\n    def exponential_moving_average(self, period):\n        return self.data['Close'].ewm(span=period, adjust=False).mean()\n\n    def relative_strength_index(self, period=14):\n        delta = self.data['Close'].diff()\n        gain = (delta.where(delta > 0, 0)).rolling(window=period).mean()\n        loss = (-delta.where(delta < 0, 0)).rolling(window=period).mean()\n        rs = gain / loss\n        return 100 - (100 / (1 + rs))\n\n    def bollinger_bands(self, period=20, num_std_dev=2):\n        ma = self.moving_average(period)\n        std_dev = self.data['Close'].rolling(window=period).std()\n        upper_band = ma + (num_std_dev * std_dev)\n        lower_band = ma - (num_std_dev * std_dev)\n        return upper_band, lower_band\n\n    def macd(self, short_period=12, long_period=26):\n        short_ema = self.exponential_moving_average(short_period)\n        long_ema = self.exponential_moving_average(long_period)\n        return short_ema - long_ema\n\n# Example usage:\n# df = pd.read_csv('your_data.csv')  # DataFrame with a 'Close' column\n# ta = TechnicalAnalysis(df)\n# print(ta.moving_average(20))\n